import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl';
import Marker from "./Marker"
import './Map.css';
import fetchFakeData from '../../api/fetchFakeData'
import ReactDOM from 'react-dom';
mapboxgl.accessToken = 'pk.eyJ1Ijoia2FrYXNoaTI1MTAiLCJhIjoiY2ttb2xld2NxMGkzdzJwbGhpcXNkMmptMiJ9.--t9iJ_IT1Vz6XLBnNcVhA';

const Map = () => {
  const mapContainerRef = useRef(null);

  // initialize map when component mounts
  useEffect(() => {
    const map = new mapboxgl.Map({
      container: mapContainerRef.current,
      // See style options here: https://docs.mapbox.com/api/maps/#styles
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [-104.9876, 39.7405],
      zoom: 12.5,

    });
    map.on('moveend', async () => {
        // get center coordinates
        const { lng, lat } = map.getCenter();
        // fetch new data
        const results = await fetchFakeData({ longitude: lng, latitude: lat });
        // iterate through the feature collection and append marker to the map for each feature
        results.features.forEach(result => {
          const { id, geometry } = result;
          // create marker node
          const markerNode = document.createElement('div');
          ReactDOM.render(<Marker id={id} />, markerNode);
          // add marker to map
          new mapboxgl.Marker(markerNode)
            .setLngLat(geometry.coordinates)
            .addTo(map);
        });
      });
    // add navigation control (the +/- zoom buttons)
    map.addControl(new mapboxgl.NavigationControl(), 'bottom-right');

    // clean up on unmount
    return () => map.remove();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return <div className="map-container" ref={mapContainerRef} />;
};

export default Map;
